 index("default", doc.sample.name);
 if(doc.grouping)                  {
 index("grouping",    doc.grouping, {"store": "yes"});
} if(doc.grouping)                  {
 index("experiment",  doc.grouping, {"store": "yes"});
} if(doc.sample.name)             {
 index("name",        doc.sample.name, {"store": "yes"});
} if(doc.sample.description)      {
 index("description", doc.sample.description, {"store": "yes"});
} if(doc.sample.id)               {
 index("id",          doc.sample.id, {"store": "yes"});
} if(doc.sample.source)           {
 index("source",      doc.sample.source, {"store": "yes"});
} if(doc.sample.owner.name)       {
 index("owner",       doc.sample.owner.name, {"store": "yes"});
} if(doc.measurement.institution)      {
 index("institution",  doc.measurement.institution, {"store": "yes"});
} if(doc.measurement.technique)        {
 index("technique",    doc.measurement.technique, {"store": "yes"});
} if(doc.measurement.date)             {
 index("date",         doc.measurement.date, {"store": "yes"});
} if(doc.measurement.requestor.name)   {
